mapper.select:
SELECT * FROM(
	SELECT rownum rnum, (SELECT m.user_id FROM member m WHERE m.user_no = F.user_no) user_id, F.* FROM (
		SELECT
		*
		FROM free
		ORDER BY free_no DESC
	) F
) FREEMAIN
WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE free set
free_views = free_views + 1
WHERE free_no = #{freeNo}

mapper.select:
	SELECT * FROM(
		SELECT 
		(SELECT m.user_id FROM member m WHERE m.user_no = F.user_no) user_id,
		(SELECT m.user_id FROM member m WHERE m.user_no = F.user_no) user_nick, F.* FROM (
			SELECT
			*
			FROM free
		) F
) FREEMAIN
WHERE free_no = #{freeBoard.freeNo}

	SELECT * FROM (
    	SELECT rownum rnum, C.* FROM (
        	SELECT * FROM campaign ORDER BY camp_no DESC
    	) C
	) CAMP
	WHERE rnum BETWEEN #{startNo} AND #{endNo}
