mapper.select:
SELECT * FROM admin
WHERE admin_id= #{adminId} and
admin_pw=#{adminPw}

SELECT * FROM (
SELECT rownum rnum, C.* FROM (
SELECT *
FROM campaign ORDER BY camp_no DESC
) C
) CAMP
WHERE rnum BETWEEN
#{startNo} AND #{endNo}

SELECT * FROM (
SELECT rownum rnum, P.* FROM (
SELECT *
FROM product ORDER BY prod_no DESC
) P
) Goods
WHERE rnum BETWEEN
#{startNo} AND #{endNo}

mapper.delete:
{CALL
BEGIN
DELETE FROM
campaign_file
WHERE camp_no = ${campNo};

DELETE FROM campaign
WHERE
camp_no = ${campNo};
END
}

mapper.select:
	SELECT * FROM(
	SELECT 
	(SELECT m.admin_id FROM admin m WHERE m.admin_no = F.admin_no) admin_id, 
	(SELECT m.admin_name FROM admin m WHERE m.admin_no = F.admin_no) admin_name, F.* FROM (
		SELECT
		*
		FROM campaign
	) F
) FREEMAIN
WHERE CAMP_NO = #{campaign.campNo}

mapper.update:
UPDATE campaign SET
camp_title = #{campTitle}, camp_state = #{campState}
WHERE camp_no = #{campNo}
