mapper.select:
SELECT * FROM(
	SELECT rownum rnum, F.* FROM (
		SELECT
		free_no, free_title, free_head, free_content, free_views, free_create
		FROM free
		ORDER BY free_no DESC
	)F
)FREE
WHERE rnum BETWEEN #{startNo} AND #{endNo}

mapper.update:
UPDATE free set
free_views = free_views + 1
WHERE free_no = #{freeNo}
