mapper.select:
	SELECT * FROM(
		SELECT rownum rnum, (SELECT m.id FROM member m WHERE m.userno = F.user_no) id, F.* FROM (
			SELECT
			*
			FROM free
	
		
			WHERE free_head = #{freeHead}
			ORDER BY free_no DESC
		
		
		
			WHERE free_head = #{freeHead}
			ORDER BY free_no DESC
		
		
		
			WHERE free_head = #{freeHead}
			ORDER BY free_no DESC
		
		
		
			ORDER BY free_no DESC
		

		
			ORDER BY free_no DESC
		
		
		
		) F
	) FREEMAIN
	WHERE rnum BETWEEN #{paging.startNo} and #{paging.endNo}
	ORDER BY free_no DESC

SELECT * FROM(
	SELECT 
	(SELECT m.id FROM member m WHERE m.userno = F.user_no) id,
	(SELECT m.id FROM member m WHERE m.userno = F.user_no) nick, F.* FROM (
		SELECT
		*
		FROM free
	) F
) FREEMAIN
WHERE free_no = #{freeBoard.freeNo}

mapper.update:
UPDATE free set
free_views = free_views + 1
WHERE free_no = #{freeNo}

UPDATE free SET
free_title = #{freeTitle}, free_content = #{freeContent}, free_head = #{freeHead}
WHERE free_no = #{freeNo}

